import { useState } from 'react';
import { Dimensions, Image, PixelRatio, Platform, SafeAreaView, ScrollView, StatusBar, StyleSheet, Text, TextInput, View } from 'react-native';


export default function App() {

  const [ state, setState] = useState('hello');
  return (
    <View>
      <View style={styles.title}>
        <Text style={styles.text}>{state} luyi</Text>
      </View>
      <View>
        <Text style={styles.text}>hello luyi</Text>
      </View>
      <View>
        <Text style={styles.text}>hello luyi</Text>
      </View>
      <TextInput onChange={e => setState(e.nativeEvent.text)} />
      <View>
        <Text>逻辑宽度：{Dimensions.get("window").width}</Text>
        <Text>屏幕像素比：{PixelRatio.get()}</Text>
        <Text>物理像素：{Dimensions.get("window").width * PixelRatio.get()}</Text>
      </View>

      <ScrollView>
        {new Array(1000).fill(0).map((item, idx) => (
          <Text key={idx}>teaching you {idx} times</Text>
        ))}
      </ScrollView>
    </View>
  )
}

const styles = StyleSheet.create({
  title: {
    alignSelf: "center",
    // transform: [{ scale: 1.5 }, { rotate: '-10deg' }],
    backgroundColor: "#f00",
    width: "100vh%"
  },
  text: {
    color: "#00f"
  }
})
